mapper.insert:
INSERT INTO user
   SET uid = #{uid}
     , upw = #{upw}
     , nm = #{nm}

mapper.select:
SELECT user_id AS userId, uid, upw, nm, created_at AS createdAt, updated_at AS updatedAt
  FROM user
 WHERE uid = #{uid}

mapper.update:
UPDATE user
SET upw = #{newPw}
WHERE user_id = #{userId}
